import React, { useState, useRef } from "react";
import { Card, CardContent } from "./ui/card";
import { Progress } from "./ui/progress";
import { CheckCircle, AlertTriangle, Upload, Shield, FileText, Download } from "lucide-react";
import axios from 'axios';

const ResumeVerification = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationResult, setVerificationResult] = useState(null);
  const [fraudScore, setFraudScore] = useState(null);
  const [fraudReasons, setFraudReasons] = useState([]);
  const [detailedReport, setDetailedReport] = useState(null);
  const fileInputRef = useRef(null);
  const [isDownloading, setIsDownloading] = useState(false);
  const [personName, setPersonName] = useState("");
  const [recentUploads, setRecentUploads] = useState([]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    handleFiles(files);
  };

  const handleFileInput = (e) => {
    const files = e.target.files;
    handleFiles(files);
  };

  const handleFiles = async (files) => {
    if (files.length > 0 && personName.trim()) {
      setIsVerifying(true);
      setVerificationResult(null);
      setFraudScore(null);
      setFraudReasons([]);
      setDetailedReport(null);

      const formData = new FormData();
      formData.append('resume', files[0]);
      formData.append('name', personName);

      try {
        const response = await axios.post('http://localhost:5000/analyze-resume', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        const fraudScore = response.data.fraud_score;
        const hasRedFlags = response.data.reasons.length > 0;
        
        // Add to recent uploads
        const newUpload = {
          name: personName,
          timestamp: new Date().toLocaleString(),
          fraudScore: fraudScore,
          status: fraudScore < 30 ? "Authentic" : fraudScore >= 70 ? "Potential Fraud" : "Suspicious"
        };
        setRecentUploads(prev => [newUpload, ...prev.slice(0, 4)]); // Keep last 5 uploads

        // More strict verification criteria
        if (fraudScore < 30 && !hasRedFlags) {
          setVerificationResult("valid");
        } else if (fraudScore >= 70 || hasRedFlags) {
          setVerificationResult("invalid");
        } else {
          setVerificationResult("suspicious");
        }

        setFraudScore(fraudScore);
        setFraudReasons(response.data.reasons);
        setDetailedReport({
          ...response.data.report,
          personName: personName // Add person name to report
        });
      } catch (error) {
        console.error('Error analyzing resume:', error);
        setVerificationResult("error");
      } finally {
        setIsVerifying(false);
      }
    } else if (!personName.trim()) {
      alert("Please enter the person's name before uploading a resume.");
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current?.click();
  };

  const handleDownloadReport = async () => {
    if (!detailedReport) return;

    setIsDownloading(true);
    try {
      console.log("Starting report download...");
      console.log("Report data:", detailedReport);  // Debug log
      
      const response = await axios.post('http://localhost:5000/generate-report', 
        { report: detailedReport },  // Send the entire detailedReport object
        {
          responseType: 'blob',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/pdf'
          }
        }
      );
      
      console.log("Response received:", response);  // Debug log
      
      if (response.status === 200) {
        // Create a blob from the PDF stream
        const blob = new Blob([response.data], { type: 'application/pdf' });
        console.log("Blob created:", blob);  // Debug log
        
        // Create and click download link
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'resume_analysis_report.pdf';
        document.body.appendChild(a);
        a.click();
        
        // Cleanup
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
        console.log("Download completed successfully");
      } else {
        throw new Error('Failed to generate PDF');
      }
    } catch (error) {
      console.error('Error downloading report:', error);
      console.error('Error details:', error.response?.data);  // Debug log
      alert('Failed to download report. Please try again.');
    } finally {
      setIsDownloading(false);
    }
  };

  return (
    <div className="min-h-screen relative bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 font-['-apple-system','BlinkMacSystemFont','Segoe UI','Roboto','Helvetica Neue','Arial','sans-serif']">
      {/* Animated background elements */}
      <div className="absolute inset-0 bg-[linear-gradient(to_right,#0f172a20,#1e293b30,#0f172a20)] pointer-events-none animate-pulse" />
      <div className="absolute inset-0 bg-[radial-gradient(#ffffff08_1px,transparent_1px)] [background-size:16px_16px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,black,transparent)] opacity-25" />
      <div className="absolute inset-0 bg-[linear-gradient(45deg,#0f172a10,#1e293b10,#0f172a10)] animate-gradient" />
      
      <div className="relative min-h-screen">
        <div className="max-w-6xl mx-auto p-4 py-8">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div>
              <h1 className="text-5xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 via-purple-400 to-pink-400 tracking-tight animate-gradient">
                AuthentiScan
              </h1>
              <p className="text-white/80 text-sm mt-1 tracking-wide">Upload and verify your resume for authenticity</p>
            </div>
            <div className="flex items-center space-x-2 bg-slate-800/50 backdrop-blur-sm px-4 py-2 rounded-full shadow-sm border border-slate-700/30">
              <Shield className="w-6 h-6 text-white" />
              <span className="text-sm font-medium text-white tracking-wide">Secure Verification</span>
            </div>
          </div>

          <div className="grid gap-4 grid-cols-1 md:grid-cols-12">
            {/* Main Upload Section */}
            <div className="md:col-span-8 space-y-4">
              <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-lg font-semibold text-white">Upload Resume</h2>
                    <span className="text-xs text-white/70 tracking-wider uppercase">PDF only</span>
                  </div>
                  
                  {/* Add name input field */}
                  <div className="mb-4">
                    <input
                      type="text"
                      placeholder="Enter person's name"
                      value={personName}
                      onChange={(e) => setPersonName(e.target.value)}
                      className="w-full px-4 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    />
                  </div>
                  
                  <div
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-all duration-200 ${
                      isDragging 
                        ? "border-indigo-400 bg-indigo-500/20" 
                        : "border-slate-700/30 bg-slate-800/20"
                    }`}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                    onDrop={handleDrop}
                  >
                    <div className="flex flex-col items-center justify-center space-y-4">
                      <div className="p-4 rounded-full bg-slate-800/10">
                        <Upload className="w-8 h-8 text-white" />
                      </div>
                      <div className="space-y-2">
                        <p className="text-lg text-white font-medium">Drag and drop your resume here</p>
                        <p className="text-sm text-white/70">or</p>
                        <button
                          onClick={handleBrowseClick}
                          className="text-sm text-white hover:text-indigo-200 font-medium tracking-wide px-4 py-2 rounded-full bg-slate-800/10 hover:bg-slate-700/20 transition-colors duration-200"
                        >
                          Browse Files
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Hidden file input */}
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    accept=".pdf"
                    onChange={handleFileInput}
                  />

                  {isVerifying && (
                    <div className="mt-4 space-y-2">
                      <div className="flex items-center justify-between text-xs text-white/70 tracking-wider">
                        <span>Analyzing Resume...</span>
                      </div>
                      <Progress value={100} className="h-1 bg-slate-700/30" />
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Verification Results */}
              {verificationResult && (
                <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                  <CardContent className="p-4">
                    <h3 className="text-lg font-semibold text-white mb-4">Analysis Results</h3>
                    <div className="space-y-4">
                      <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium text-white tracking-wide">Fraud Score</span>
                          <span className={`text-lg font-bold ${
                            fraudScore < 30 ? 'text-emerald-400' :
                            fraudScore >= 70 ? 'text-red-400' :
                            'text-amber-400'
                          }`}>
                            {fraudScore}%
                          </span>
                        </div>
                        <p className="text-xs text-white/70 tracking-wide">
                          {fraudScore < 30 ? 'Resume appears to be authentic' :
                           fraudScore >= 70 ? 'High probability of fraud detected' :
                           'Resume requires further verification'}
                        </p>
                      </div>

                      {fraudReasons.length > 0 && (
                        <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                          <h4 className="text-sm font-medium text-white tracking-wide mb-2">Red Flags</h4>
                          <ul className="space-y-2">
                            {fraudReasons.map((reason, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <AlertTriangle className="w-4 h-4 text-amber-400 mt-0.5" />
                                <span className="text-xs text-white/70 tracking-wide">{reason}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}

                      {detailedReport && (
                        <div className="space-y-4">
                          <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                            <h4 className="text-sm font-medium text-white tracking-wide mb-2">Detailed Analysis</h4>
                            <div className="space-y-3">
                              <div className="flex items-center justify-between text-xs">
                                <span className="text-white/70">Risk Level</span>
                                <span className={`font-medium ${
                                  detailedReport.summary.risk_level === 'High' ? 'text-red-400' :
                                  detailedReport.summary.risk_level === 'Medium' ? 'text-amber-400' :
                                  'text-emerald-400'
                                }`}>
                                  {detailedReport.summary.risk_level}
                                </span>
                              </div>
                              <div className="flex items-center justify-between text-xs">
                                <span className="text-white/70">Total Red Flags</span>
                                <span className="text-white font-medium">{detailedReport.summary.total_red_flags}</span>
                              </div>
                            </div>
                          </div>

                          <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                            <h4 className="text-sm font-medium text-white tracking-wide mb-2">Improvement Suggestions</h4>
                            <div className="space-y-3">
                              {detailedReport.recommendations.map((rec, index) => (
                                <div key={index} className="flex items-start space-x-2">
                                  <CheckCircle className="w-4 h-4 text-emerald-400 mt-0.5" />
                                  <span className="text-xs text-white/70 tracking-wide">{rec}</span>
                                </div>
                              ))}
                            </div>
                          </div>

                          <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                            <h4 className="text-sm font-medium text-white tracking-wide mb-2">Section Analysis</h4>
                            <div className="space-y-2">
                              {Object.entries(detailedReport.detailed_analysis.sections).map(([section, status]) => (
                                <div key={section} className="flex items-center justify-between text-xs">
                                  <span className="text-white/70 capitalize">{section}</span>
                                  <span className={`font-medium ${
                                    status === "Present" ? "text-emerald-400" : "text-red-400"
                                  }`}>
                                    {status}
                                  </span>
                                </div>
                              ))}
                            </div>
                          </div>

                          <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                            <h4 className="text-sm font-medium text-white tracking-wide mb-2">Red Flags</h4>
                            <div className="space-y-2">
                              {detailedReport.detailed_analysis.red_flags.map((flag, index) => (
                                <div key={index} className="flex items-start space-x-2">
                                  <AlertTriangle className="w-4 h-4 text-amber-400 mt-0.5" />
                                  <span className="text-xs text-white/70 tracking-wide">{flag}</span>
                                </div>
                              ))}
                            </div>
                          </div>

                          <button
                            onClick={handleDownloadReport}
                            disabled={isDownloading}
                            className={`w-full flex items-center justify-center space-x-2 text-sm text-white hover:text-indigo-200 font-medium tracking-wide px-4 py-2 rounded-lg bg-slate-800/20 hover:bg-slate-700/30 transition-colors duration-200 border border-slate-700/30 ${isDownloading ? 'opacity-50 cursor-not-allowed' : ''}`}
                          >
                            {isDownloading ? (
                              <>
                                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                                <span>Generating Report...</span>
                              </>
                            ) : (
                              <>
                                <Download className="w-4 h-4" />
                                <span>Download Detailed Report</span>
                              </>
                            )}
                          </button>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>

            {/* Sidebar */}
            <div className="md:col-span-4 space-y-4">
              {/* Status Card */}
              <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-sm font-medium text-white tracking-wide">Verification Status</h3>
                    {verificationResult === "valid" ? (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-emerald-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-emerald-500/20 text-emerald-400 rounded-full tracking-wider uppercase border border-emerald-400/20">
                          Authentic
                        </span>
                      </div>
                    ) : verificationResult === "invalid" ? (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-red-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-red-500/20 text-red-400 rounded-full tracking-wider uppercase border border-red-400/20">
                          Potential Fraud
                        </span>
                      </div>
                    ) : verificationResult === "suspicious" ? (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-amber-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-amber-500/20 text-amber-400 rounded-full tracking-wider uppercase border border-amber-400/20">
                          Suspicious
                        </span>
                      </div>
                    ) : (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-slate-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-slate-500/20 text-slate-400 rounded-full tracking-wider uppercase border border-slate-400/20">
                          Pending
                        </span>
                      </div>
                    )}
                  </div>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-white/70 tracking-wide">Last Analyzed</span>
                      <span className="text-white font-medium tracking-wide">
                        {verificationResult ? new Date().toLocaleTimeString() : 'Not analyzed'}
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Recent Uploads Card */}
              <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-sm font-medium text-white tracking-wide">Recent Uploads</h3>
                    <FileText className="w-4 h-4 text-white" />
                  </div>
                  <div className="space-y-3">
                    {recentUploads.map((upload, index) => (
                      <div key={index} className="p-3 bg-slate-800/20 rounded-lg border border-slate-700/30">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium text-white">{upload.name}</span>
                          <span className={`text-xs font-medium px-2 py-1 rounded-full ${
                            upload.status === "Authentic" ? "bg-emerald-500/20 text-emerald-400" :
                            upload.status === "Potential Fraud" ? "bg-red-500/20 text-red-400" :
                            "bg-amber-500/20 text-amber-400"
                          }`}>
                            {upload.status}
                          </span>
                        </div>
                        <div className="flex items-center justify-between text-xs">
                          <span className="text-white/70">{upload.timestamp}</span>
                          <span className="text-white/70">{upload.fraudScore}% Risk</span>
                        </div>
                      </div>
                    ))}
                    {recentUploads.length === 0 && (
                      <div className="text-center text-white/50 text-sm">
                        No recent uploads
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>

              {/* Detailed Analysis Card */}
              {detailedReport && (
                <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-sm font-medium text-white tracking-wide">Section Analysis</h3>
                      <FileText className="w-4 h-4 text-white" />
                    </div>
                    <div className="space-y-3">
                      {Object.entries(detailedReport.detailed_analysis.sections).map(([section, status]) => (
                        <div key={section} className="flex items-center justify-between text-xs">
                          <span className="text-white/70 capitalize">{section}</span>
                          <span className={`font-medium ${
                            status === "Present" ? "text-emerald-400" : "text-red-400"
                          }`}>
                            {status}
                          </span>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeVerification;
