import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, AlertTriangle, BarChart2, GraduationCap, Briefcase } from 'lucide-react';

const ResumeAnalytics = () => {
  const [analyticsData, setAnalyticsData] = useState({
    technicalSkills: { status: 'pending', score: 0 },
    softSkills: { status: 'pending', score: 0 },
    employment: { status: 'pending', score: 0 },
    education: { status: 'pending', score: 0 },
    overallScore: 0
  });

  useEffect(() => {
    // Function to check for updates
    const checkForUpdates = () => {
      const storedData = localStorage.getItem('resumeAnalytics');
      if (storedData) {
        const parsedData = JSON.parse(storedData);
        setAnalyticsData(parsedData);
      }
    };

    // Check immediately
    checkForUpdates();

    // Set up interval to check for updates every second
    const interval = setInterval(checkForUpdates, 1000);

    // Cleanup interval on component unmount
    return () => clearInterval(interval);
  }, []);

  const getStatusIcon = (status) => {
    if (status === 'verified') {
      return <CheckCircle className="w-5 h-5 text-green-400" />;
    }
    return <AlertTriangle className="w-5 h-5 text-yellow-400" />;
  };

  const getStatusText = (status) => {
    if (status === 'verified') {
      return <span className="text-green-400 font-medium">Verified</span>;
    }
    return <span className="text-yellow-400 font-medium">Pending</span>;
  };

  return (
    <div className="min-h-screen bg-slate-900 text-white pt-4 px-8 relative overflow-hidden group/bg">
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-4">
              <div className="relative group">
                <div className="absolute inset-0 bg-gradient-to-br from-indigo-500/10 to-purple-500/10 rounded-full blur-xl transition-all duration-500" />
                <img
                  className="h-12 w-auto"
                  src="/AuthentiScan-Logo.png"
                  alt="AuthentiScan Logo"
                />
              </div>
              <div>
                <h1 className="text-5xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 via-purple-400 to-pink-400 tracking-tight animate-gradient">
                  AuthentiScan
                </h1>
                <p className="text-white/80 text-sm mt-1 tracking-wide">Upload and verify your resume for authenticity</p>
              </div>
            </div>
            <div className="flex items-center space-x-2 bg-slate-800/50 backdrop-blur-sm px-4 py-2 rounded-full shadow-sm border border-slate-700/30">
              <Shield className="w-6 h-6 text-white" />
              <span className="text-sm font-medium text-white tracking-wide">Secure Analysis</span>
            </div>
          </div>

          <div className="grid gap-6 grid-cols-1 md:grid-cols-2 mt-8">
            <div className="bg-slate-800/50 backdrop-blur-sm p-6 rounded-xl border border-slate-700/30">
              <h2 className="text-xl font-semibold text-white mb-4 flex items-center">
                <BarChart2 className="w-6 h-6 text-indigo-400 mr-2" />
                Skill Analysis
              </h2>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(analyticsData.technicalSkills.status)}
                    <span className="text-gray-300">Technical Skills</span>
                  </div>
                  {getStatusText(analyticsData.technicalSkills.status)}
                </div>
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(analyticsData.softSkills.status)}
                    <span className="text-gray-300">Soft Skills</span>
                  </div>
                  {getStatusText(analyticsData.softSkills.status)}
                </div>
              </div>
            </div>

            <div className="bg-slate-800/50 backdrop-blur-sm p-6 rounded-xl border border-slate-700/30">
              <h2 className="text-xl font-semibold text-white mb-4 flex items-center">
                <GraduationCap className="w-6 h-6 text-purple-400 mr-2" />
                Experience Validation
              </h2>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(analyticsData.employment.status)}
                    <span className="text-gray-300">Previous Employment</span>
                  </div>
                  {getStatusText(analyticsData.employment.status)}
                </div>
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(analyticsData.education.status)}
                    <span className="text-gray-300">Education</span>
                  </div>
                  {getStatusText(analyticsData.education.status)}
                </div>
              </div>
            </div>
          </div>

          <div className="mt-8 bg-slate-800/50 backdrop-blur-sm p-6 rounded-xl border border-slate-700/30">
            <h2 className="text-xl font-semibold text-white mb-4 flex items-center">
              <Briefcase className="w-6 h-6 text-pink-400 mr-2" />
              Overall Score
            </h2>
            <div className="flex items-center">
              <div className="w-24 h-24 rounded-full bg-gradient-to-br from-indigo-500/20 to-purple-500/20 flex items-center justify-center">
                <span className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-purple-400">
                  {analyticsData.overallScore}%
                </span>
              </div>
              <div className="ml-6">
                <p className="text-gray-300">Resume Authenticity Score</p>
                <p className="text-sm text-gray-400">
                  {analyticsData.overallScore === 0 
                    ? "Upload a resume to get started" 
                    : "Based on multiple verification factors"}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeAnalytics;
