import React, { useState, useRef } from "react";
import { Card, CardContent } from "./ui/card";
import { Button } from "./ui/button";
import { Progress } from "./ui/progress";
import { CheckCircle, AlertTriangle, Upload, FileText, Download, X, Loader2 } from "lucide-react";

const ResumeVerification = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationResult, setVerificationResult] = useState(null);
  const [recentUploads, setRecentUploads] = useState([
    { name: "resume1.pdf", date: "2024-04-12", status: "verified" },
    { name: "resume2.docx", date: "2024-04-11", status: "pending" }
  ]);
  const fileInputRef = useRef(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    handleFiles(files);
  };

  const handleFileInput = (e) => {
    const files = e.target.files;
    handleFiles(files);
  };

  const handleFiles = (files) => {
    if (files.length > 0) {
      setIsVerifying(true);
      setUploadProgress(0);
      
      // Simulate upload and verification process
      const interval = setInterval(() => {
        setUploadProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setIsVerifying(false);
            setVerificationResult("valid");
            return 100;
          }
          return prev + 10;
        });
      }, 500);
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Resume Verification System</h1>
          <p className="text-gray-600">Upload your resume for instant verification and fraud detection</p>
        </div>

        <div className="grid gap-6 grid-cols-1 md:grid-cols-3">
          {/* Upload Section */}
          <Card className="md:col-span-2 transform transition-all duration-300 hover:shadow-lg">
            <CardContent className="p-8">
              <h2 className="text-2xl font-semibold text-gray-800 mb-6">Upload Resume</h2>
              <div
                className={`border-2 border-dashed rounded-xl p-8 text-center transition-all duration-300 ${
                  isDragging ? "border-blue-500 bg-blue-50" : "border-blue-300"
                }`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                <Upload className="mx-auto mb-4 text-blue-500 w-12 h-12" />
                <p className="text-gray-600 mb-2">
                  Drag and drop your resume here, or{" "}
                  <span
                    className="text-blue-600 cursor-pointer hover:text-blue-700 font-medium"
                    onClick={handleBrowseClick}
                  >
                    Browse
                  </span>
                </p>
                <p className="text-sm text-gray-500">Supported formats: PDF, DOCX (Max 5MB)</p>
                <input
                  type="file"
                  ref={fileInputRef}
                  className="hidden"
                  accept=".pdf,.docx"
                  onChange={handleFileInput}
                />
              </div>

              {isVerifying && (
                <div className="mt-6 space-y-4">
                  <div className="flex items-center justify-between text-sm text-gray-600">
                    <span>Verification Progress</span>
                    <span>{uploadProgress}%</span>
                  </div>
                  <Progress value={uploadProgress} className="h-2" />
                  <div className="flex items-center justify-center text-blue-600">
                    <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                    <span>Verifying your resume...</span>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Status Cards */}
          <div className="space-y-6">
            <Card className="transform transition-all duration-300 hover:shadow-lg">
              <CardContent className="p-6">
                <h3 className="text-lg font-medium text-gray-800 mb-4">Verification Status</h3>
                <div className={`p-4 rounded-lg text-center ${
                  verificationResult === "valid" 
                    ? "bg-green-100 text-green-800" 
                    : verificationResult === "invalid"
                    ? "bg-red-100 text-red-800"
                    : "bg-gray-100 text-gray-800"
                }`}>
                  {verificationResult ? (
                    <div className="flex items-center justify-center">
                      {verificationResult === "valid" ? (
                        <CheckCircle className="w-6 h-6 mr-2" />
                      ) : (
                        <X className="w-6 h-6 mr-2" />
                      )}
                      <span className="font-bold">
                        {verificationResult === "valid" ? "VALID" : "INVALID"}
                      </span>
                    </div>
                  ) : (
                    <span className="font-medium">Not Verified</span>
                  )}
                </div>
              </CardContent>
            </Card>

            <Card className="transform transition-all duration-300 hover:shadow-lg">
              <CardContent className="p-6">
                <h3 className="text-lg font-medium text-gray-800 mb-4">Recent Uploads</h3>
                <ul className="space-y-3">
                  {recentUploads.map((upload, index) => (
                    <li key={index} className="flex items-center justify-between p-2 rounded-lg hover:bg-gray-50">
                      <div className="flex items-center">
                        <FileText className="w-5 h-5 text-gray-500 mr-2" />
                        <span className="text-sm text-gray-700">{upload.name}</span>
                      </div>
                      <div className="flex items-center">
                        <span className={`text-xs px-2 py-1 rounded-full ${
                          upload.status === "verified" 
                            ? "bg-green-100 text-green-800" 
                            : "bg-yellow-100 text-yellow-800"
                        }`}>
                          {upload.status}
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>

            <Card className="transform transition-all duration-300 hover:shadow-lg">
              <CardContent className="p-6">
                <h3 className="text-lg font-medium text-gray-800 mb-4">Sections Verified</h3>
                <ul className="space-y-3">
                  <li className="flex items-center text-sm text-gray-700">
                    <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
                    Education
                  </li>
                  <li className="flex items-center text-sm text-gray-700">
                    <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
                    Experience
                  </li>
                  <li className="flex items-center text-sm text-gray-700">
                    <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
                    Skills
                  </li>
                </ul>
                <Button className="mt-6 w-full bg-blue-600 hover:bg-blue-700">
                  <Download className="w-4 h-4 mr-2" />
                  Download Report
                </Button>
              </CardContent>
            </Card>
          </div>

          {/* Discrepancies Section */}
          <Card className="md:col-span-3 transform transition-all duration-300 hover:shadow-lg">
            <CardContent className="p-6">
              <h3 className="text-lg font-medium text-gray-800 mb-4">Discrepancies Detected</h3>
              <div className="grid md:grid-cols-3 gap-4">
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="flex items-center text-red-800 mb-2">
                    <AlertTriangle className="w-5 h-5 mr-2" />
                    <span className="font-medium">Incomplete Job History</span>
                  </div>
                  <p className="text-sm text-red-600">Missing employment details for 2020-2021</p>
                </div>
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="flex items-center text-red-800 mb-2">
                    <AlertTriangle className="w-5 h-5 mr-2" />
                    <span className="font-medium">Extended Employment Gap</span>
                  </div>
                  <p className="text-sm text-red-600">Gap of 8 months detected between jobs</p>
                </div>
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="flex items-center text-red-800 mb-2">
                    <AlertTriangle className="w-5 h-5 mr-2" />
                    <span className="font-medium">Unverified Certification</span>
                  </div>
                  <p className="text-sm text-red-600">AWS Certification could not be verified</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default ResumeVerification;
