import React, { useState, useEffect } from 'react';
import { Progress } from './ui/progress';

const PaymentModal = ({ amountUSD, onCancel, onSuccess, isPaying }) => {
  const [upiPin, setUpiPin] = useState('');
  const [amount, setAmount] = useState(amountUSD);
  const [error, setError] = useState('');
  const [timer, setTimer] = useState(60); // 60 seconds timer

  useEffect(() => {
    if (timer > 0 && isPaying) {
      const interval = setInterval(() => setTimer(t => t - 1), 1000);
      return () => clearInterval(interval);
    }
  }, [timer, isPaying]);

  const handlePay = async () => {
    setError('');
    if (!upiPin || upiPin.length < 4) {
      setError('Enter a valid 4 or 6 digit UPI PIN.');
      return;
    }
    if (isNaN(amount) || Number(amount) < 10) {
      setError('Amount must be at least $10.00');
      return;
    }
    if (timer <= 0) {
      setError('Payment session expired. Please try again.');
      return;
    }
    await onSuccess(Number(amount), upiPin);
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50" onClick={onCancel}>
      <div className="bg-white rounded-lg p-8 shadow-xl flex flex-col items-center w-96" onClick={e => e.stopPropagation()}>
        <h4 className="text-lg font-semibold mb-2 text-gray-900">Pay & Download (UPI)</h4>
        <p className="mb-4 text-gray-700">Enter your UPI PIN and amount to proceed. Minimum $10.00</p>
        <div className="w-full mb-4">
          <label className="block text-gray-700 text-sm mb-1">Amount (USD)</label>
          <input
            type="number"
            min="10"
            step="0.01"
            className="w-full px-3 py-2 border border-gray-300 rounded-md mb-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={amount}
            onChange={e => setAmount(e.target.value)}
            disabled={isPaying}
          />
        </div>
        <div className="w-full mb-4">
          <label className="block text-gray-700 text-sm mb-1">UPI PIN</label>
          <input
            type="password"
            maxLength={6}
            minLength={4}
            pattern="[0-9]{4,6}"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={upiPin}
            onChange={e => setUpiPin(e.target.value.replace(/[^0-9]/g, ''))}
            disabled={isPaying}
          />
        </div>
        <div className="w-full mb-4">
          <Progress value={(timer / 60) * 100} className="h-2" />
          <div className="text-xs text-gray-500 text-right">{timer} seconds left</div>
        </div>
        {error && <div className="text-red-500 text-xs mb-2">{error}</div>}
        <button
          className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold px-6 py-2 rounded-md mb-2 w-full"
          disabled={isPaying || timer <= 0}
          onClick={handlePay}
        >{isPaying ? 'Processing...' : 'Pay & Download'}</button>
        <button
          className="text-sm text-gray-500 hover:text-gray-700 mt-2"
          disabled={isPaying}
          onClick={onCancel}
        >Cancel</button>
      </div>
    </div>
  );
};

export default PaymentModal;
