import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent } from './ui/card';
import { useAuth } from '../context/AuthContext';
import { UserPlus, User, Mail, Phone, Building, LogIn, Shield } from 'lucide-react';
const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    company: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { signup } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords don't match!");
      return;
    }

    setError('');
    setIsLoading(true);

    try {
      const result = await signup({
        name: `${formData.firstName} ${formData.lastName}`,
        email: formData.email,
        phone: formData.phone,
        company: formData.company
      });

      if (result.success) {
        navigate('/');
      } else {
        setError(result.error || 'Signup failed');
      }
    } catch (error) {
      setError('An error occurred during signup');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-slate-900 flex flex-col justify-center items-center px-4 py-8 relative overflow-hidden">
      {/* Combined animated dotted grid and blurred blobs background */}
      <div className="fixed inset-0 pointer-events-none z-0">
        {/* Dotted grid SVG */}
        <svg width="100%" height="100%" className="absolute inset-0 animate-gradient">
          <defs>
            <pattern id="dots" x="0" y="0" width="40" height="40" patternUnits="userSpaceOnUse">
              <circle cx="8" cy="8" r="2" fill="#67e8f9" opacity="0.10" />
            </pattern>
          </defs>
          <rect width="100%" height="100%" fill="url(#dots)" />
        </svg>
        {/* Animated blurred blobs */}
        <div className="absolute top-0 left-0 w-[28rem] h-[28rem] bg-cyan-400/30 rounded-full mix-blend-lighten filter blur-2xl animate-blob" style={{animationDelay: '0s'}}></div>
        <div className="absolute bottom-0 right-0 w-[32rem] h-[32rem] bg-violet-500/40 rounded-full mix-blend-lighten filter blur-2xl animate-blob" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/2 left-1/2 w-[30rem] h-[30rem] bg-blue-500/30 rounded-full mix-blend-lighten filter blur-2xl animate-blob" style={{animationDelay: '4s', transform: 'translate(-50%, -50%)'}}></div>
      </div>
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          <Header badgeText="Create Account" />

          <div className="mt-12 flex justify-center">
            <div className="w-[500px]">
              <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                <CardContent className="p-5">
                  <div className="flex items-center justify-center mb-4">
                    <div className="p-0 rounded-full bg-gradient-to-r from-purple-500/20 to-pink-500/20 backdrop-blur-sm border border-purple-500/30 shadow-lg group hover:shadow-purple-500/20 hover:scale-105 transition-all duration-300">
                      <img 
                        src="/user-logo.png" 
                        alt="User Logo" 
                        className="w-16 h-16 rounded-full object-cover group-hover:rotate-6 transition-transform duration-300"
                      />
                    </div>
                  </div>
                  <h2 className="text-xl font-bold text-center text-white mb-5">Create Your Account</h2>
                  {error && (
                    <div className="mb-4 p-3 rounded-lg bg-red-500/10 border border-red-500/50 text-red-200 text-sm">
                      {error}
                    </div>
                  )}
                  <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-white/70 mb-1">First Name</label>
                        <div className="relative">
                          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <User className="h-4 w-4 text-slate-400" />
                          </div>
                          <input
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={handleChange}
                            className="w-full pl-9 pr-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                            required
                            disabled={isLoading}
                          />
                        </div>
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-white/70 mb-1">Last Name</label>
                        <div className="relative">
                          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <User className="h-4 w-4 text-slate-400" />
                          </div>
                          <input
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                            className="w-full pl-9 pr-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                            required
                            disabled={isLoading}
                          />
                        </div>
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70 mb-1">Email</label>
                      <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <Mail className="h-4 w-4 text-slate-400" />
                        </div>
                        <input
                          type="email"
                          name="email"
                          value={formData.email}
                          onChange={handleChange}
                          className="w-full pl-9 pr-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                          required
                          disabled={isLoading}
                        />
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70 mb-1">Phone Number</label>
                      <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <Phone className="h-4 w-4 text-slate-400" />
                        </div>
                        <input
                          type="tel"
                          name="phone"
                          value={formData.phone}
                          onChange={handleChange}
                          className="w-full pl-9 pr-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                          required
                          disabled={isLoading}
                        />
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70 mb-1">Company</label>
                      <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <Building className="h-4 w-4 text-slate-400" />
                        </div>
                        <input
                          type="text"
                          name="company"
                          value={formData.company}
                          onChange={handleChange}
                          className="w-full pl-9 pr-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                          required
                          disabled={isLoading}
                        />
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70 mb-1">Password</label>
                      <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="w-full px-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                        disabled={isLoading}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70 mb-1">Confirm Password</label>
                      <input
                        type="password"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        className="w-full px-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                        disabled={isLoading}
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full flex items-center justify-center space-x-2 text-sm text-white hover:text-indigo-200 font-medium tracking-wide px-4 py-2 rounded-lg bg-slate-800/20 hover:bg-slate-700/30 transition-colors duration-200 border border-slate-700/30 disabled:opacity-50 disabled:cursor-not-allowed"
                      disabled={isLoading}
                    >
                      {isLoading ? 'Creating Account...' : 'Create Account'}
                    </button>
                  </form>
                  <p className="mt-6 text-center">
                    <a 
                      href="/login" 
                      className="group inline-flex items-center gap-2 text-sm hover:scale-105 transition-all duration-200"
                    >
                      <span className="text-white/70">Already have an account?</span>
                      <span className="inline-flex items-center gap-1.5 text-indigo-400 hover:text-indigo-300 font-medium">
                        Login
                        <LogIn 
                          className="w-4 h-4 transition-transform duration-200 group-hover:rotate-12" 
                          strokeWidth={2.5}
                        />
                      </span>
                    </a>
                  </p>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="max-w-6xl mx-auto relative mt-8 mb-4">
        <div className="bg-slate-800/30 backdrop-blur-sm rounded-xl p-4 shadow-xl border border-slate-700/30">
          <div className="text-center text-sm text-gray-400">
            <p>Â© {new Date().getFullYear()} SnapVault. All rights reserved.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
