const ResumeVerification = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationResult, setVerificationResult] = useState(null);
  const [recentUploads] = useState([
    { name: "resume1.pdf", date: "2024-04-12", status: "verified" },
    { name: "resume2.docx", date: "2024-04-11", status: "pending" }
  ]);
  const fileInputRef = useRef(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    handleFiles(files);
  };

  const handleFileInput = (e) => {
    const files = e.target.files;
    handleFiles(files);
  };

  const handleFiles = (files) => {
    if (files.length > 0) {
      setIsVerifying(true);
      setUploadProgress(0);
      
      const interval = setInterval(() => {
        setUploadProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setIsVerifying(false);
            setVerificationResult("valid");
            return 100;
          }
          return prev + 10;
        });
      }, 500);
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-2xl font-bold text-slate-800">Resume Verification</h1>
            <p className="text-slate-600 text-sm">Upload and verify your resume for authenticity</p>
          </div>
          <div className="flex items-center space-x-2">
            <Shield className="w-6 h-6 text-blue-500" />
            <span className="text-sm font-medium text-slate-700">Secure Verification</span>
          </div>
        </div>

        <div className="grid gap-4 grid-cols-1 md:grid-cols-12">
          {/* Main Upload Section */}
          <div className="md:col-span-8 space-y-4">
            <Card className="bg-white shadow-sm hover:shadow-md transition-shadow duration-200">
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-slate-800">Upload Resume</h2>
                  <span className="text-xs text-slate-500">Max 5MB â€¢ PDF, DOCX</span>
                </div>
                
                <div
                  className={`border-2 border-dashed rounded-lg p-4 text-center transition-all duration-200 ${
                    isDragging ? "border-blue-400 bg-blue-50" : "border-slate-200"
                  }`}
                  onDragOver={handleDragOver}
                  onDragLeave={handleDragLeave}
                  onDrop={handleDrop}
                >
                  <div className="flex items-center justify-center space-x-2 mb-2">
                    <Upload className="w-5 h-5 text-slate-400" />
                    <span className="text-sm text-slate-600">Drag and drop your resume here</span>
                  </div>
                  <div className="flex items-center justify-center space-x-2">
                    <span className="text-sm text-slate-500">or</span>
                    <button
                      onClick={handleBrowseClick}
                      className="text-sm text-blue-600 hover:text-blue-700 font-medium"
                    >
                      Browse Files
                    </button>
                  </div>
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    accept=".pdf,.docx"
                    onChange={handleFileInput}
                  />
                </div>

                {isVerifying && (
                  <div className="mt-4 space-y-2">
                    <div className="flex items-center justify-between text-xs text-slate-600">
                      <span>Verifying...</span>
                      <span>{uploadProgress}%</span>
                    </div>
                    <Progress value={uploadProgress} className="h-1" />
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Verification Results */}
            <Card className="bg-white shadow-sm hover:shadow-md transition-shadow duration-200">
              <CardContent className="p-4">
                <h3 className="text-lg font-semibold text-slate-800 mb-4">Verification Results</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="bg-green-50 p-3 rounded-lg hover:bg-green-100 transition-colors duration-200">
                    <div className="flex items-center space-x-2 mb-2">
                      <CheckCircle className="w-5 h-5 text-green-500" />
                      <span className="text-sm font-medium text-green-800">Education</span>
                    </div>
                    <p className="text-xs text-green-600">Verified with institutions</p>
                  </div>
                  <div className="bg-green-50 p-3 rounded-lg hover:bg-green-100 transition-colors duration-200">
                    <div className="flex items-center space-x-2 mb-2">
                      <CheckCircle className="w-5 h-5 text-green-500" />
                      <span className="text-sm font-medium text-green-800">Experience</span>
                    </div>
                    <p className="text-xs text-green-600">Employment history verified</p>
                  </div>
                  <div className="bg-green-50 p-3 rounded-lg hover:bg-green-100 transition-colors duration-200">
                    <div className="flex items-center space-x-2 mb-2">
                      <CheckCircle className="w-5 h-5 text-green-500" />
                      <span className="text-sm font-medium text-green-800">Skills</span>
                    </div>
                    <p className="text-xs text-green-600">Certifications validated</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="md:col-span-4 space-y-4">
            {/* Status Card */}
            <Card className="bg-white shadow-sm hover:shadow-md transition-shadow duration-200">
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-sm font-medium text-slate-800">Verification Status</h3>
                  {verificationResult === "valid" ? (
                    <span className="px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded-full">
                      Verified
                    </span>
                  ) : (
                    <span className="px-2 py-1 text-xs font-medium bg-yellow-100 text-yellow-800 rounded-full">
                      Pending
                    </span>
                  )}
                </div>
                <div className="space-y-3">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-slate-600">Last Verified</span>
                    <span className="text-slate-800 font-medium">2 hours ago</span>
                  </div>
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-slate-600">Confidence Score</span>
                    <span className="text-slate-800 font-medium">98%</span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Recent Uploads */}
            <Card className="bg-white shadow-sm hover:shadow-md transition-shadow duration-200">
              <CardContent className="p-4">
                <h3 className="text-sm font-medium text-slate-800 mb-4">Recent Uploads</h3>
                <div className="space-y-3">
                  {recentUploads.map((upload, index) => (
                    <div key={index} className="flex items-center justify-between p-2 rounded-lg hover:bg-slate-50 transition-colors duration-200">
                      <div className="flex items-center space-x-2">
                        <FileText className="w-4 h-4 text-slate-400" />
                        <span className="text-sm text-slate-700">{upload.name}</span>
                      </div>
                      <span className={`text-xs px-2 py-1 rounded-full ${
                        upload.status === "verified" 
                          ? "bg-green-100 text-green-800" 
                          : "bg-yellow-100 text-yellow-800"
                      }`}>
                        {upload.status}
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Discrepancies */}
            <Card className="bg-white shadow-sm hover:shadow-md transition-shadow duration-200">
              <CardContent className="p-4">
                <div className="flex items-center space-x-2 mb-4">
                  <AlertTriangle className="w-5 h-5 text-red-500" />
                  <h3 className="text-sm font-medium text-slate-800">Discrepancies</h3>
                </div>
                <div className="space-y-3">
                  <div className="flex items-start space-x-2">
                    <AlertTriangle className="w-4 h-4 text-red-500 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-red-800">Incomplete Job History</p>
                      <p className="text-xs text-red-600">Missing 2020-2021 employment details</p>
                    </div>
                  </div>
                  <div className="flex items-start space-x-2">
                    <AlertTriangle className="w-4 h-4 text-red-500 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-red-800">Employment Gap</p>
                      <p className="text-xs text-red-600">8 months gap detected</p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeVerification;
