import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { Shield, Image as ImageIcon, UploadCloud, XCircle, Download as DownloadIcon, ZoomIn } from 'lucide-react';
import Header from './Header';
import { useAuth } from '../context/AuthContext';

const Publications = () => {
  const [lightboxPub, setLightboxPub] = useState(null);
  const [showDownloadOptions, setShowDownloadOptions] = useState(false);
  const { isAuthenticated } = useAuth();
  const [publications, setPublications] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [gallery, setGallery] = useState([]);
  const [previewUrl, setPreviewUrl] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  // Fetch gallery images from backend
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!isAuthenticated || !token) return;
    const fetchGallery = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/photos', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        setGallery(data.photos || []);
      } catch (err) {
        setGallery([]);
      }
    };
    fetchGallery();
  }, [isAuthenticated]);

  useEffect(() => {
    const loadData = () => {
      try {
        const storedData = localStorage.getItem('publicationsData');
        if (storedData) {
          const parsedData = JSON.parse(storedData);
          setPublications(Array.isArray(parsedData) ? parsedData : []);
        }
      } catch (error) {
        console.error('Error loading publications data:', error);
        setPublications([]);
      }
    };
    loadData();
  }, []);

  // For previewing selected image from gallery
  useEffect(() => {
    if (!selectedImage) {
      setPreviewUrl('');
      return;
    }
    setPreviewUrl(`http://localhost:5000/uploads/${selectedImage.filename}`);
  }, [selectedImage]);

  // Handle image selection from gallery
  const handleImageSelect = (e) => {
    const imgId = e.target.value;
    const img = gallery.find(g => g._id === imgId);
    setSelectedImage(img || null);
  };

  // Handle publication submit
  const handlePublish = (e) => {
    e.preventDefault();
    setError('');
    if (!selectedImage || !title.trim() || !description.trim()) {
      setError('Please select an image, and provide a title and description.');
      return;
    }
    const newPub = {
      id: Date.now(),
      image: selectedImage.filename,
      title: title.trim(),
      description: description.trim(),
      createdAt: new Date().toISOString(),
      isPublic: true
    };
    const updated = [newPub, ...publications];
    setPublications(updated);
    localStorage.setItem('publicationsData', JSON.stringify(updated));
    setSelectedImage(null);
    setTitle('');
    setDescription('');
    setPreviewUrl('');
  };

  const handleDownloadImage = async (quality) => {
    if (!lightboxPub) return;
    const filename = lightboxPub.image;
    const url = quality === 'high' ? `http://localhost:5000/uploads/${filename}` : `http://localhost:5000/uploads/lowq/${filename}`;
    try {
      const response = await axios.get(url, { responseType: 'blob' });
      const blobUrl = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = blobUrl;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
      window.URL.revokeObjectURL(blobUrl);
    } catch (err) {
      alert('Failed to download image.');
    }
  };

  return (
    <div className="min-h-screen text-white pt-4 px-8 relative overflow-hidden group/bg">
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          <Header badgeText="Publications" />

          <h2 className="text-2xl font-bold text-white mb-8 flex items-center">
            <Shield className="w-6 h-6 text-indigo-400 mr-2" />
            Publications List
          </h2>

          <div className="mb-6">
            <div className="text-sm text-pink-300 bg-pink-900/20 border border-pink-700/30 rounded-lg px-4 py-2 mb-2">
              <span className="font-semibold">Notice:</span> All images published here are <span className="font-bold">public</span> and can be viewed even without logging in.
            </div>
          </div>

          {isAuthenticated && (
            <form onSubmit={handlePublish} className="mb-8 bg-slate-900/40 border border-slate-700/30 rounded-xl p-6 flex flex-col md:flex-row items-center gap-6">
              <div className="flex flex-col items-center">
                <label className="flex flex-col items-center">
                  <select
                    className="w-40 bg-slate-800/50 border border-slate-700/30 rounded-md p-2 mb-2 text-white"
                    value={selectedImage ? selectedImage._id : ''}
                    onChange={handleImageSelect}
                  >
                    <option value="">Select from your gallery</option>
                    {gallery.map(img => (
                      <option key={img._id} value={img._id}>{img.originalname || img.filename}</option>
                    ))}
                  </select>
                  <div className="w-28 h-28 flex items-center justify-center bg-slate-800/30 border-2 border-dashed border-slate-600 rounded-lg mb-2">
                    {previewUrl ? (
                      <img src={previewUrl} alt="Preview" className="object-cover w-full h-full rounded-lg" />
                    ) : (
                      <ImageIcon className="w-10 h-10 text-slate-400" />
                    )}
                  </div>
                  <span className="text-xs text-slate-400">Choose uploaded image</span>
                </label>
              </div>
              <div className="flex-1 w-full">
                <input
                  type="text"
                  className="w-full bg-slate-800/50 border border-slate-700/30 rounded-md p-2 mb-2 text-white"
                  placeholder="Title"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                />
                <textarea
                  className="w-full bg-slate-800/50 border border-slate-700/30 rounded-md p-2 mb-2 text-white"
                  placeholder="Description"
                  value={description}
                  onChange={e => setDescription(e.target.value)}
                  rows={2}
                />
                {error && <div className="text-red-400 mb-2 text-sm">{error}</div>}
                <button
                  type="submit"
                  className="flex items-center gap-2 bg-pink-600 hover:bg-pink-700 text-white font-semibold px-4 py-2 rounded-md transition-colors"
                >
                  <UploadCloud className="w-5 h-5" /> Publish
                </button>
              </div>
            </form>
          )}

          {publications.length === 0 ? (
            <div className="text-gray-300 text-lg text-center py-12">No publications found.</div>
          ) : (
            <div className="overflow-x-auto">
              <div className="grid gap-6 grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
                {publications.map((pub, idx) => (
                  <div key={pub.id || idx} className="bg-slate-800/50 border border-slate-700/30 rounded-lg p-4 flex flex-col items-center shadow hover:shadow-lg transition">
                    <div className="w-full h-48 flex items-center justify-center bg-slate-900/30 rounded-md mb-4 overflow-hidden cursor-pointer group" onClick={() => setLightboxPub(pub)} title="View publication">
                      {pub.image ? (
                        <img src={`http://localhost:5000/uploads/${pub.image}`} alt={pub.title} className="object-cover w-full h-full group-hover:scale-105 transition-transform duration-200" />
                      ) : (
                        <ImageIcon className="w-12 h-12 text-slate-500" />
                      )}
                      <span className="absolute right-2 bottom-2 bg-slate-900/80 p-1 rounded-full text-white opacity-80"><ZoomIn className="w-5 h-5" /></span>
                    </div>
                    <div className="w-full">
                      <h3 className="text-lg font-bold text-white mb-1 truncate" title={pub.title}>{pub.title}</h3>
                      <p className="text-sm text-slate-300 mb-2 line-clamp-3" title={pub.description}>{pub.description}</p>
                      <div className="text-xs text-slate-400 mt-2">Published: {pub.createdAt ? new Date(pub.createdAt).toLocaleString() : ''}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Lightbox */}
      {lightboxPub && (
        <div className="fixed inset-0 bg-black/70 flex items-center justify-center">
          <div className="bg-slate-800/50 rounded-2xl p-4 shadow-xl border border-slate-700/30 w-full max-w-3xl">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold text-white">{lightboxPub.title}</h2>
              <button className="bg-slate-900/50 p-2 rounded-full" onClick={() => setLightboxPub(null)}>
                <XCircle className="w-6 h-6 text-white" />
              </button>
            </div>
            <div className="w-full h-96 flex items-center justify-center bg-slate-900/30 rounded-md mb-4 overflow-hidden">
              {lightboxPub.image ? (
                <img src={`http://localhost:5000/uploads/${lightboxPub.image}`} alt={lightboxPub.title} className="object-cover w-full h-full" />
              ) : (
                <ImageIcon className="w-12 h-12 text-slate-500" />
              )}
            </div>
            <div className="flex justify-between items-center">
              <p className="text-sm text-slate-300">{lightboxPub.description}</p>
              <div className="flex gap-2">
                <button className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-4 py-2 rounded-md transition-colors" onClick={() => setShowDownloadOptions(true)}>
                  <DownloadIcon className="w-5 h-5" /> Download
                </button>
                {showDownloadOptions && (
                  <div className="bg-slate-900/50 p-2 rounded-md">
                    <button className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-4 py-2 rounded-md transition-colors mb-2" onClick={() => handleDownloadImage('high')}>
                      High Quality
                    </button>
                    <button className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-4 py-2 rounded-md transition-colors" onClick={() => handleDownloadImage('low')}>
                      Low Quality
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Footer */}
      <div className="max-w-6xl mx-auto relative mt-8 mb-4">
        <div className="bg-slate-800/30 backdrop-blur-sm rounded-xl p-4 shadow-xl border border-slate-700/30">
          <div className="text-center text-sm text-gray-400">
            <p> {new Date().getFullYear()} SnapVault. All rights reserved.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

