import React, { useState, useEffect } from 'react';
import { Shield, AlertTriangle, CheckCircle, XCircle } from 'lucide-react';
import Header from './Header';

const Publications = () => {
  const [publications, setPublications] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [galleryImages, setGalleryImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    const loadData = () => {
      try {
        const storedData = localStorage.getItem('publicationsData');
        if (storedData) {
          const parsedData = JSON.parse(storedData);
          setPublications(Array.isArray(parsedData) ? parsedData : []);
        }
      } catch (error) {
        console.error('Error loading publications data:', error);
        setPublications([]);
      }
    };
    loadData();
    // Fetch public gallery images (stubbed here)
    // Replace with real fetch if available
    const storedGallery = localStorage.getItem('galleryImages');
    if (storedGallery) {
      const images = JSON.parse(storedGallery);
      setGalleryImages(Array.isArray(images) ? images.filter(img => img.isPublic) : []);
    }
  }, []);

  const handleAddPublication = () => {
    if (!selectedImage || !title) return;
    const newPub = {
      imageUrl: selectedImage.url,
      title,
      description
    };
    const updated = [...publications, newPub];
    setPublications(updated);
    localStorage.setItem('publicationsData', JSON.stringify(updated));
    setShowAddModal(false);
    setSelectedImage(null);
    setTitle("");
    setDescription("");
  };

  return (
    <div className="min-h-screen text-white pt-4 px-8 relative overflow-hidden group/bg">
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          <Header badgeText="Publications" />

          <div className="flex justify-between items-center mb-8">
            <h2 className="text-2xl font-bold text-white flex items-center">
              <Shield className="w-6 h-6 text-indigo-400 mr-2" />
              Publications List
            </h2>
            <button
              className="bg-pink-500 hover:bg-pink-600 text-white px-4 py-2 rounded-lg font-semibold shadow transition"
              onClick={() => setShowAddModal(true)}
            >
              Add Photo from Gallery
            </button>
          </div>
          {showAddModal && (
            <div className="fixed inset-0 bg-black/40 z-50 flex items-center justify-center">
              <div className="bg-slate-900 rounded-xl p-8 w-full max-w-lg shadow-xl relative">
                <button
                  className="absolute top-4 right-4 text-gray-400 hover:text-white"
                  onClick={() => setShowAddModal(false)}
                >
                  <XCircle className="w-6 h-6" />
                </button>
                <h3 className="text-xl font-semibold mb-4 text-white">Add Public Photo</h3>
                {galleryImages.length === 0 ? (
                  <div className="text-gray-300">No public images available in your gallery.</div>
                ) : (
                  <>
                    <div className="mb-4">
                      <label className="block text-gray-300 mb-2">Select Image</label>
                      <div className="flex flex-wrap gap-4 max-h-40 overflow-y-auto">
                        {galleryImages.map((img, idx) => (
                          <img
                            key={idx}
                            src={img.url}
                            alt={img.title || `Gallery Image ${idx+1}`}
                            className={`w-20 h-20 object-cover rounded-lg border-2 cursor-pointer ${selectedImage && selectedImage.url === img.url ? 'border-pink-500' : 'border-transparent'}`}
                            onClick={() => setSelectedImage(img)}
                          />
                        ))}
                      </div>
                    </div>
                    <div className="mb-4">
                      <label className="block text-gray-300 mb-2">Title</label>
                      <input
                        type="text"
                        className="w-full px-3 py-2 rounded bg-slate-800 text-white border border-slate-700 focus:outline-none"
                        value={title}
                        onChange={e => setTitle(e.target.value)}
                      />
                    </div>
                    <div className="mb-4">
                      <label className="block text-gray-300 mb-2">Description</label>
                      <textarea
                        className="w-full px-3 py-2 rounded bg-slate-800 text-white border border-slate-700 focus:outline-none"
                        rows={3}
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                      />
                    </div>
                    <button
                      className="bg-indigo-500 hover:bg-indigo-600 text-white px-4 py-2 rounded-lg font-semibold shadow transition disabled:opacity-50"
                      onClick={handleAddPublication}
                      disabled={!selectedImage || !title}
                    >
                      Add Publication
                    </button>
                  </>
                )}
              </div>
            </div>
          )}
          {publications.length === 0 ? (
            <div className="text-gray-300 text-lg text-center py-12">No publications found.</div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-slate-700">
                <thead>
                  <tr>
                    <th className="px-4 py-2 text-left text-gray-400 font-semibold">Image</th>
                    <th className="px-4 py-2 text-left text-gray-400 font-semibold">Title</th>
                    <th className="px-4 py-2 text-left text-gray-400 font-semibold">Description</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-800">
                  {publications.map((pub, idx) => (
                    <tr key={idx} className="hover:bg-slate-700/30 transition">
                      <td className="px-4 py-2">
                        <img src={pub.imageUrl} alt={pub.title} className="w-20 h-20 object-cover rounded" />
                      </td>
                      <td className="px-4 py-2 text-white max-w-xs truncate" title={pub.title}>{pub.title}</td>
                      <td className="px-4 py-2 text-gray-300 max-w-xs truncate" title={pub.description}>{pub.description}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Footer */}
      <div className="max-w-6xl mx-auto relative mt-8 mb-4">
        <div className="bg-slate-800/30 backdrop-blur-sm rounded-xl p-4 shadow-xl border border-slate-700/30">
          <div className="text-center text-sm text-gray-400">
            <p> {new Date().getFullYear()} SnapVault. All rights reserved.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Publications;
