import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent } from './ui/card';
import Header from './Header';
import { UserPlus } from 'lucide-react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [hoveredField, setHoveredField] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        email,
        password
      });

      // Store the token in localStorage
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));

      // Redirect to home page
      navigate('/');
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred during login');
    } finally {
      setIsLoading(false);
    }
  };

  const getLabelColor = (fieldName) => {
    if (hoveredField === fieldName) {
      return 'text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-600';
    }
    return 'text-white/70';
  };

  return (
    <div className="min-h-screen bg-slate-900 text-white pt-4 px-8 relative overflow-hidden group/bg">
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          <Header badgeText="Secure Login" />

          <div className="mt-12 flex justify-center">
            <div className="w-[400px]">
              <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                <CardContent className="p-5">
                  <div className="flex items-center justify-center mb-6">
                    <img 
                      src="/AuthentiScan-Logo.png" 
                      alt="AuthentiScan Logo" 
                      className="h-16 w-auto object-contain filter brightness-100"
                    />
                  </div>
                  <h2 className="text-xl font-bold text-center text-white mb-5">Login to Your Account</h2>
                  {error && (
                    <div className="mb-4 p-3 rounded-lg bg-red-500/10 border border-red-500/50 text-red-200 text-sm">
                      {error}
                    </div>
                  )}
                  <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                      <label 
                        className={`block text-sm font-medium mb-1 transition-colors duration-200 ${getLabelColor('email')}`}
                        onMouseEnter={() => setHoveredField('email')}
                        onMouseLeave={() => setHoveredField(null)}
                      >
                        Email
                      </label>
                      <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="w-full px-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                        disabled={isLoading}
                      />
                    </div>
                    <div>
                      <label 
                        className={`block text-sm font-medium mb-1 transition-colors duration-200 ${getLabelColor('password')}`}
                        onMouseEnter={() => setHoveredField('password')}
                        onMouseLeave={() => setHoveredField(null)}
                      >
                        Password
                      </label>
                      <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full px-3 py-2 rounded-lg bg-slate-800/20 border border-slate-700/30 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                        disabled={isLoading}
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full flex items-center justify-center space-x-2 text-sm text-white hover:text-indigo-200 font-medium tracking-wide px-4 py-2 rounded-lg bg-slate-800/20 hover:bg-slate-700/30 transition-colors duration-200 border border-slate-700/30 disabled:opacity-50 disabled:cursor-not-allowed"
                      disabled={isLoading}
                    >
                      {isLoading ? 'Logging in...' : 'Login'}
                    </button>
                  </form>
                  <p className="mt-6 text-center">
                    <a 
                      href="/signup" 
                      className="group inline-flex items-center gap-2 text-sm hover:scale-105 transition-all duration-200"
                    >
                      <span className="text-white/70">Don't have an account?</span>
                      <span className="inline-flex items-center gap-1.5 text-indigo-400 hover:text-indigo-300 font-medium">
                        Create account
                        <UserPlus 
                          className="w-4 h-4 transition-transform duration-200 group-hover:rotate-12" 
                          strokeWidth={2.5}
                        />
                      </span>
                    </a>
                  </p>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="max-w-6xl mx-auto relative mt-8 mb-4">
        <div className="bg-slate-800/30 backdrop-blur-sm rounded-xl p-4 shadow-xl border border-slate-700/30">
          <div className="text-center text-sm text-gray-400">
            <p>Â© {new Date().getFullYear()} AuthentiScan. All rights reserved.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
