import React, { useState } from 'react';
import { Card, CardContent } from './ui/card';
import { Progress } from './ui/progress';
import Header from './Header';
import { Star, GitBranch, Users, BookOpen } from 'lucide-react';

const GitHubAnalyzer = () => {
  const [username, setUsername] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);
  const [showHeatmapModal, setShowHeatmapModal] = useState(false);

  const handleAnalyze = async (e) => {
    e.preventDefault();
    setError('');
    setResult(null);
    setLoading(true);
    try {
      const response = await fetch('/api/github-analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username })
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Unknown error');
      setResult(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-slate-900 text-white pt-4 px-8 relative overflow-hidden group/bg">
      {/* Interactive background elements */}
      <div className="fixed inset-0 transition-all duration-700 group-hover/bg:backdrop-blur-sm">
        <div className="absolute inset-0 opacity-20 group-hover/bg:opacity-30 transition-opacity duration-700">
          <div className="absolute top-0 -left-4 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-blob"></div>
          <div className="absolute top-0 -right-4 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-2000"></div>
          <div className="absolute -bottom-8 left-20 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-blob animation-delay-4000"></div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto relative mt-16">
        {/* Content */}
        <div className="bg-slate-800/50 backdrop-blur-md rounded-2xl p-8 shadow-xl border border-slate-700/30">
          <Header badgeText="GitHub Analysis" />
          <form onSubmit={handleAnalyze} className="flex flex-col md:flex-row items-center gap-4 mb-8">
            <input
              type="text"
              placeholder="Enter GitHub username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              className="flex-1 px-4 py-2 rounded-lg bg-slate-900 text-white border border-slate-700/30 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              required
            />
            <button
              type="submit"
              className="px-6 py-2 rounded-lg bg-indigo-500 hover:bg-indigo-600 text-white font-semibold transition-colors duration-200 shadow-md"
              disabled={loading}
            >
              {loading ? 'Analyzing...' : 'Analyze'}
            </button>
          </form>
          {error && <div className="text-red-500 mb-4 font-medium">{error}</div>}

          <div className="grid gap-4 grid-cols-1 md:grid-cols-12 mt-12">
            {/* Main Analysis Section */}
            <div className="md:col-span-8 space-y-4">
              {/* Score Card */}
              {result && (
                <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-lg font-semibold text-white">GitHub Score</h3>
                      <span className="text-2xl font-bold text-indigo-400">{result.score}/100</span>
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Summary Card */}
              {result && (
                <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
                  <CardContent className="p-4">
                    <h3 className="text-lg font-semibold text-white mb-4">Analysis Results</h3>
                    <div className="space-y-4">
                      <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                        <h4 className="text-sm font-medium text-white tracking-wide mb-2">Summary (Paragraph)</h4>
                        <p className="text-xs text-white/70 tracking-wide">{result.summary_paragraph || "No summary paragraph returned from analysis."}</p>
                      </div>
                      <div className="bg-slate-800/20 backdrop-blur-sm p-4 rounded-lg border border-slate-700/30">
                        <h4 className="text-sm font-medium text-white tracking-wide mb-2">Detailed Summary</h4>
                        {/* GitHub Stats Row */}
                        {result.summary && (() => {
                          // Extract stats from summary using regex for both 'Stars: 1' and '1 Stars' (case-insensitive)
                          const stars = result.summary.match(/Stars\s*[:\-]?\s*(\d+)/i) || result.summary.match(/(\d+)\s+Stars?/i);
                          const forks = result.summary.match(/Forks\s*[:\-]?\s*(\d+)/i) || result.summary.match(/(\d+)\s+Forks?/i);
                          const followers = result.summary.match(/Followers\s*[:\-]?\s*(\d+)/i) || result.summary.match(/(\d+)\s+Followers?/i);
                          const repos = result.summary.match(/Public Repositories\s*[:\-]?\s*(\d+)/i) || result.summary.match(/(\d+)\s+Public Repositories?/i);
                          return (
                            <div className="flex flex-wrap gap-6 mb-4 w-full justify-center">
                              {stars && (
                                <div className="flex items-center gap-2 bg-slate-900/70 px-3 py-1 rounded-lg">
                                  <Star className="w-5 h-5 text-yellow-400" />
                                  <span className="text-white font-semibold text-base">{stars[1]}</span>
                                  <span className="text-xs text-white/70">Stars</span>
                                </div>
                              )}
                              {forks && (
                                <div className="flex items-center gap-2 bg-slate-900/70 px-3 py-1 rounded-lg">
                                  <GitBranch className="w-5 h-5 text-green-400" />
                                  <span className="text-white font-semibold text-base">{forks[1]}</span>
                                  <span className="text-xs text-white/70">Forks</span>
                                </div>
                              )}
                              {followers && (
                                <div className="flex items-center gap-2 bg-slate-900/70 px-3 py-1 rounded-lg">
                                  <Users className="w-5 h-5 text-blue-400" />
                                  <span className="text-white font-semibold text-base">{followers[1]}</span>
                                  <span className="text-xs text-white/70">Followers</span>
                                </div>
                              )}
                              {repos && (
                                <div className="flex items-center gap-2 bg-slate-900/70 px-3 py-1 rounded-lg">
                                  <BookOpen className="w-5 h-5 text-purple-400" />
                                  <span className="text-white font-semibold text-base">{repos[1]}</span>
                                  <span className="text-xs text-white/70">Repos</span>
                                </div>
                              )}
                            </div>
                          );
                        })()}
                        <pre className="bg-slate-900 text-gray-200 p-4 rounded overflow-x-auto whitespace-pre-wrap text-me font-sans" style={{ fontFamily: 'Poppins, var(--font-sans), sans-serif' }}>
  {result.summary || "No detailed summary returned from analysis."}
</pre>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>

            {/* Sidebar */}
            <div className="md:col-span-4 space-y-4">
              {/* Heatmap Card */}
              {result && (
  <>
    <Card className="bg-slate-800/30 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-slate-700/30">
      <CardContent className="p-4 flex flex-col items-center">
        <h3 className="text-base font-semibold text-green-300 mb-2">Contribution Heatmap</h3>
        <img
          src={`data:image/png;base64,${result.heatmap}`}
          alt="Contribution Heatmap"
          className="rounded-lg border border-slate-700 cursor-pointer"
          style={{ maxWidth: '100%' }}
          onClick={() => setShowHeatmapModal(true)}
        />
      </CardContent>
    </Card>
    {/* Modal for heatmap */}
    {showHeatmapModal && (
      <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/0" onClick={() => setShowHeatmapModal(false)}>
        <div className="bg-slate-900 rounded-xl p-6 shadow-2xl max-w-[1000px] w-full flex flex-col items-center relative" onClick={e => e.stopPropagation()}>
  <button className="absolute top-2 right-2 text-gray-300 hover:text-white text-2xl font-bold" onClick={() => setShowHeatmapModal(false)}>&times;</button>
  <div className="flex justify-center items-center w-full h-full p-4">
    <img
      src={`data:image/png;base64,${result.heatmap}`}
      alt="Contribution Heatmap Large"
      className="rounded-lg border border-slate-700 bg-slate-800 shadow-lg max-w-[900px] max-h-[65vh] object-contain"
      style={{ display: 'block', margin: '0 auto' }}
    />
  </div>
</div>
      </div>
    )}
  </>
)}
              {/* Placeholder for future sidebar cards (recent analyses, etc.) */}
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="max-w-6xl mx-auto relative mt-8 mb-4">
        <div className="bg-slate-800/30 backdrop-blur-sm rounded-xl p-4 shadow-xl border border-slate-700/30">
          <div className="text-center text-sm text-gray-400">
            <p>Â© {new Date().getFullYear()} AuthentiScan. All rights reserved.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GitHubAnalyzer;
