import React, { useState, useRef } from "react";
import { Card, CardContent } from "./ui/card";
import { Progress } from "./ui/progress";
import { CheckCircle, AlertTriangle, Upload, FileText, Shield } from "lucide-react";

const ResumeVerification = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationResult, setVerificationResult] = useState(null);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const fileInputRef = useRef(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    handleFiles(files);
  };

  const handleFileInput = (e) => {
    const files = e.target.files;
    handleFiles(files);
  };

  const handleFiles = (files) => {
    if (files.length > 0) {
      setIsVerifying(true);
      setUploadProgress(0);
      
      const newFile = {
        name: files[0].name,
        date: new Date().toISOString().split('T')[0],
        status: 'pending'
      };
      
      setUploadedFiles(prev => [newFile, ...prev]);
      
      const interval = setInterval(() => {
        setUploadProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            setIsVerifying(false);
            setVerificationResult("valid");
            setUploadedFiles(prev => prev.map(file => 
              file.name === newFile.name ? { ...file, status: 'verified' } : file
            ));
            return 100;
          }
          return prev + 10;
        });
      }, 500);
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="min-h-screen relative bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 font-['-apple-system','BlinkMacSystemFont','Segoe UI','Roboto','Helvetica Neue','Arial','sans-serif']">
      {/* Animated background elements */}
      <div className="absolute inset-0 bg-[linear-gradient(to_right,#4f46e510,#ffffff20,#ec489910)] pointer-events-none animate-pulse" />
      <div className="absolute inset-0 bg-[radial-gradient(#ffffff05_1px,transparent_1px)] [background-size:16px_16px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,black,transparent)] opacity-25" />
      <div className="absolute inset-0 bg-[linear-gradient(45deg,#4f46e505,#ec489905,#4f46e505)] animate-gradient" />
      
      <div className="relative">
        <div className="max-w-6xl mx-auto p-4">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div>
              <h1 className="text-4xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-white to-indigo-200 tracking-tight font-['Outfit']">
                AuthentiScan
              </h1>
              <p className="text-white/80 text-sm mt-1 tracking-wide">Upload and verify your resume for authenticity</p>
            </div>
            <div className="flex items-center space-x-2 bg-white/20 backdrop-blur-sm px-4 py-2 rounded-full shadow-sm border border-white/30">
              <Shield className="w-6 h-6 text-white" />
              <span className="text-sm font-medium text-white tracking-wide">Secure Verification</span>
            </div>
          </div>

          <div className="grid gap-4 grid-cols-1 md:grid-cols-12">
            {/* Main Upload Section */}
            <div className="md:col-span-8 space-y-4">
              <Card className="bg-white/10 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-white/20">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-lg font-semibold text-white">Upload Resume</h2>
                    <span className="text-xs text-white/70 tracking-wider uppercase">Max 5MB • PDF, DOCX</span>
                  </div>
                  
                  <div
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-all duration-200 ${
                      isDragging 
                        ? "border-indigo-400 bg-indigo-500/20" 
                        : "border-white/30 bg-white/10"
                    }`}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                    onDrop={handleDrop}
                  >
                    <div className="flex flex-col items-center justify-center space-y-4">
                      <div className="p-4 rounded-full bg-white/10">
                        <Upload className="w-8 h-8 text-white" />
                      </div>
                      <div className="space-y-2">
                        <p className="text-lg text-white font-medium">Drag and drop your resume here</p>
                        <p className="text-sm text-white/70">or</p>
                        <button
                          onClick={handleBrowseClick}
                          className="text-sm text-white hover:text-indigo-200 font-medium tracking-wide px-4 py-2 rounded-full bg-white/10 hover:bg-white/20 transition-colors duration-200"
                        >
                          Browse Files
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Hidden file input */}
                  <input
                    type="file"
                    ref={fileInputRef}
                    className="hidden"
                    accept=".pdf,.docx"
                    onChange={handleFileInput}
                  />

                  {isVerifying && (
                    <div className="mt-4 space-y-2">
                      <div className="flex items-center justify-between text-xs text-white/70 tracking-wider">
                        <span>Verifying...</span>
                        <span>{uploadProgress}%</span>
                      </div>
                      <Progress value={uploadProgress} className="h-1 bg-white/20" />
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Verification Results */}
              <Card className="bg-white/10 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-white/20">
                <CardContent className="p-4">
                  <h3 className="text-lg font-semibold text-white mb-4">Verification Results</h3>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div className="bg-white/10 backdrop-blur-sm p-4 rounded-lg hover:bg-white/20 transition-colors duration-200 border border-white/20">
                      <div className="flex items-center space-x-2 mb-2">
                        <CheckCircle className="w-5 h-5 text-emerald-400" />
                        <span className="text-sm font-medium text-white tracking-wide">Education</span>
                      </div>
                      <p className="text-xs text-white/70 tracking-wide">Verified with institutions</p>
                    </div>
                    <div className="bg-white/10 backdrop-blur-sm p-4 rounded-lg hover:bg-white/20 transition-colors duration-200 border border-white/20">
                      <div className="flex items-center space-x-2 mb-2">
                        <CheckCircle className="w-5 h-5 text-emerald-400" />
                        <span className="text-sm font-medium text-white tracking-wide">Experience</span>
                      </div>
                      <p className="text-xs text-white/70 tracking-wide">Employment history verified</p>
                    </div>
                    <div className="bg-white/10 backdrop-blur-sm p-4 rounded-lg hover:bg-white/20 transition-colors duration-200 border border-white/20">
                      <div className="flex items-center space-x-2 mb-2">
                        <CheckCircle className="w-5 h-5 text-emerald-400" />
                        <span className="text-sm font-medium text-white tracking-wide">Skills</span>
                      </div>
                      <p className="text-xs text-white/70 tracking-wide">Certifications validated</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Sidebar */}
            <div className="md:col-span-4 space-y-4">
              {/* Status Card */}
              <Card className="bg-white/10 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-white/20">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-sm font-medium text-white tracking-wide">Verification Status</h3>
                    {verificationResult === "valid" ? (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-emerald-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-emerald-500/20 text-emerald-400 rounded-full tracking-wider uppercase border border-emerald-400/20 hover:bg-emerald-500/30 transition-colors duration-200 shadow-[0_0_10px_rgba(52,211,153,0.2)]">
                          Verified
                        </span>
                      </div>
                    ) : (
                      <div className="flex items-center space-x-1.5">
                        <div className="w-2 h-2 rounded-full bg-amber-400 animate-pulse" />
                        <span className="px-3 py-1.5 text-xs font-semibold bg-amber-500/20 text-amber-400 rounded-full tracking-wider uppercase border border-amber-400/20 hover:bg-amber-500/30 transition-colors duration-200 shadow-[0_0_10px_rgba(251,191,36,0.2)]">
                          Pending
                        </span>
                      </div>
                    )}
                  </div>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-white/70 tracking-wide">Last Verified</span>
                      <span className="text-white font-medium tracking-wide">2 hours ago</span>
                    </div>
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-white/70 tracking-wide">Confidence Score</span>
                      <span className="text-white font-medium tracking-wide">98%</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Recent Uploads */}
              <Card className="bg-white/10 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-white/20">
                <CardContent className="p-4">
                  <div className="flex items-center space-x-2 mb-4">
                    <FileText className="w-5 h-5 text-white" />
                    <h3 className="text-sm font-medium text-white tracking-wide">Recent Uploads</h3>
                  </div>
                  {uploadedFiles.length > 0 ? (
                    <div className="space-y-3">
                      {uploadedFiles.map((upload, index) => (
                        <div key={index} className="flex items-center justify-between p-2 rounded-lg hover:bg-white/10 transition-colors duration-200">
                          <div className="flex items-center space-x-2">
                            <FileText className="w-4 h-4 text-white/70" />
                            <span className="text-sm text-white tracking-wide">{upload.name}</span>
                          </div>
                          {upload.status === "verified" ? (
                            <div className="flex items-center space-x-1.5">
                              <div className="w-1.5 h-1.5 rounded-full bg-emerald-400 animate-pulse" />
                              <span className="px-2 py-1 text-xs font-semibold bg-emerald-500/20 text-emerald-400 rounded-full tracking-wider uppercase border border-emerald-400/20 hover:bg-emerald-500/30 transition-colors duration-200 shadow-[0_0_8px_rgba(52,211,153,0.2)]">
                                {upload.status}
                              </span>
                            </div>
                          ) : (
                            <div className="flex items-center space-x-1.5">
                              <div className="w-1.5 h-1.5 rounded-full bg-amber-400 animate-pulse" />
                              <span className="px-2 py-1 text-xs font-semibold bg-amber-500/20 text-amber-400 rounded-full tracking-wider uppercase border border-amber-400/20 hover:bg-amber-500/30 transition-colors duration-200 shadow-[0_0_8px_rgba(251,191,36,0.2)]">
                                {upload.status}
                              </span>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="flex flex-col items-center justify-center py-6 space-y-2">
                      <FileText className="w-8 h-8 text-white/50" />
                      <p className="text-sm text-white/70 text-center">No files uploaded yet</p>
                      <p className="text-xs text-white/50 text-center">Upload a file to see it here</p>
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Discrepancies */}
              <Card className="bg-white/10 backdrop-blur-sm shadow-lg hover:shadow-xl transition-all duration-200 border border-white/20">
                <CardContent className="p-4">
                  <div className="flex items-center space-x-2 mb-3">
                    <AlertTriangle className="w-5 h-5 text-amber-400" />
                    <h3 className="text-sm font-medium text-white tracking-wide">Discrepancies</h3>
                  </div>
                  <div className="space-y-4">
                    <div>
                      <div className="flex items-center space-x-2 mb-1">
                        <AlertTriangle className="w-4 h-4 text-amber-400" />
                        <p className="text-base font-medium text-white">Incomplete Job History</p>
                      </div>
                      <p className="text-sm text-white/70 ml-6 tracking-wide">Missing 2020-2021 employment details</p>
                    </div>
                    <div>
                      <div className="flex items-center space-x-2 mb-1">
                        <AlertTriangle className="w-4 h-4 text-amber-400" />
                        <p className="text-base font-medium text-white">Employment Gap</p>
                      </div>
                      <p className="text-sm text-white/70 ml-6 tracking-wide">8 months gap detected</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeVerification;
