import React, { createContext, useState, useContext } from 'react';

const AnalyticsContext = createContext();

export const AnalyticsProvider = ({ children }) => {
  const [analyticsData, setAnalyticsData] = useState({
    technicalSkills: { status: 'pending', score: 0 },
    softSkills: { status: 'pending', score: 0 },
    employment: { status: 'pending', score: 0 },
    education: { status: 'pending', score: 0 },
    overallScore: 0,
    lastUpdated: null
  });

  const updateAnalyticsData = (newData) => {
    setAnalyticsData({
      ...newData,
      lastUpdated: new Date().toISOString()
    });
  };

  return (
    <AnalyticsContext.Provider value={{ analyticsData, updateAnalyticsData }}>
      {children}
    </AnalyticsContext.Provider>
  );
};

export const useAnalytics = () => {
  const context = useContext(AnalyticsContext);
  if (!context) {
    throw new Error('useAnalytics must be used within an AnalyticsProvider');
  }
  return context;
};
